import React, { useState, useEffect } from "react";
import { useSpring, animated } from "react-spring";
import { useMeasure } from "react-use";
import style from "./accordion.module.css";
import {Card} from "@material-ui/core";
import DoubleChevron from '../icons/DoubleChevrons.svg'

const Test = () => {
  const defaultHeight = "100px";

  // Manages the open or cloased state of the accordion
  const [open, toggle] = useState(false);

  // The height of the content inside of the accordion
  const [contentHeight, setContentHeight] = useState(defaultHeight);

  // Gets the height of the element (ref)
  const [ref, { height }] = useMeasure();

  // Animations
  const expand = useSpring({
    config: { friction: 10 },
    height: open ? `${contentHeight}px` : defaultHeight
  });
  const spin = useSpring({
    config: { friction: 10 },
    transform: open ? "rotate(180deg)" : "rotate(0deg)"
  });

  useEffect(() => {
    //Sets initial height
    setContentHeight(height);

    //Adds resize event listener
    window.addEventListener("resize", setContentHeight(height));

    // Clean-up
    return window.removeEventListener("resize", setContentHeight(height));
  }, [height]);

  return (
    <div className={style.wrapper}>
      <animated.div className={style.accordion} style={expand}>
        <div ref={ref} className={style.content}>
          <Card>
          ba do do do ba do why
        </Card>
        </div>
      </animated.div>
      <animated.button
        className={style.expand}
        onClick={() => toggle(!open)}
        style={spin}
      >
        <img src={DoubleChevron}/>
      </animated.button>
    </div>
  );
};

export default Test;
