import React, { useEffect } from 'react';
import { Card, Typography, Button, IconButton, Container, makeStyles } from '@material-ui/core';
import { Helmet } from 'react-helmet';
import $ from 'jquery';
import LivePerson from '../media/LivePersonLogo.png';
import SocialSquare from '../components/SocialSquare';
import TwitterIcon from '../icons/TwitterIcon';
import MediumIcon from '../icons/MediumIcon';
import LinkedInIcon from '../icons/LinkedIn';
import PlaneIcon from '../icons/PlaneIcon';




const useStyles = makeStyles((theme) => ({

  linkButton: {
    marginTop: 10,
    backgroundColor: '#005ECF',
    color: '#fff',
    fontWeight: 'bold',
    textTransform: 'none',
    width: '33%',
    boxShadow: '0px 2px 2px lightgray',
  },

  icon: {
    height: 200,
    width: 200,
  },

  button: {
    marginTop: 10,
    marginLeft: 5,
    marginRight: 5,
    '&:hover': {
        backgroundColor: theme.palette.primary.main,
        boxShadow: '0px 0px 0px gray',
    },
    backgroundColor: theme.palette.primary.main,
    textTransform: 'none',
    pointerEvents: 'none',
  },

}));

function Home(){

  var baseUrl = "https://api.api.ai/v1/";
  var accessToken = "d40a2e1786324a019851c794082aaaa1";

  const send = (query) => {
    var text = query;
    $.ajax({
      type: "POST",
      url: baseUrl + "query?v=2180101",
      contentType: "application/json; charset=utf-8",
      dataType: "json",
      headers: {
        "Authorization": "Bearer " + accessToken
      },
      data: JSON.stringify({ query: text, lang: "en", sessionId: "sam" }),
      success: function(data) {
        var timeout = 0;
        if($.inArray(text, window.history)){
            timeout = 1000 * ((Math.random() * 2) + 0.5);
        }
        window.history.push(text);
        setTimeout(setResponse, timeout, data);
      }
    });
  }

  function setResponse(val) {
    var botString = "Sam-Bot: ";
    botString = botString.fontcolor("red");
    var newString = '<span class="responseData">'+ botString + val.result.fulfillment.speech + '</span><br><br>';
    newString = newString.replace(/\s+/g,' ').trim();
    // $(".chatBox").append("\n" + newString + "\n");
  }

  const classes = useStyles();

  return(


    <Container maxWidth="lg" style={{marginTop: 125, marginBottom: 400}}>


    <Helmet>
      <body style="
      background-image: url('../pbackground.png');
      background-repeat: no-repeat;
      background-size: cover;"
      />
    </Helmet>

    <div style={{textAlign: 'center'}}>

        <Typography variant="h4">
          Sam Bot
        </Typography>


      {/* <a href="https://medium.com/@samuelokirby" target="_blank">
        <SocialSquare title={'Medium'}>
          <MediumIcon className={classes.icon} width={100} height={100} fill={"#242331"}/>
        </SocialSquare>
      </a> */}



    </div>

    </Container>

  )
}

export default Home;
