import React, { useEffect, useState } from 'react';
import { Card, Typography, Button, IconButton, Container, makeStyles, Input } from '@material-ui/core';
import { Helmet } from 'react-helmet';
import $ from 'jquery';
import LivePerson from '../media/LivePersonLogo.png';
import SocialSquare from '../components/SocialSquare';
import TwitterIcon from '../icons/TwitterIcon';
import MediumIcon from '../icons/MediumIcon';
import LinkedInIcon from '../icons/LinkedIn';
import PlaneIcon from '../icons/PlaneIcon';
import ChatBody from '../components/ChatBody';




const useStyles = makeStyles((theme) => ({

  linkButton: {
    marginTop: 10,
    backgroundColor: '#005ECF',
    color: '#fff',
    fontWeight: 'bold',
    textTransform: 'none',
    width: '33%',
    boxShadow: '0px 2px 2px lightgray',
  },

  icon: {
    height: 200,
    width: 200,
  },

  button: {
    marginTop: 10,
    marginLeft: 5,
    marginRight: 5,
    '&:hover': {
        backgroundColor: theme.palette.primary.main,
        boxShadow: '0px 0px 0px gray',
    },
    backgroundColor: theme.palette.primary.main,
    textTransform: 'none',
    pointerEvents: 'none',
  },

}));

function Home(){

  var baseUrl = "https://api.api.ai/v1/";
  var accessToken = "d40a2e1786324a019851c794082aaaa1";

  const [history, updateHistory] = useState([]);

  const [input, setInput] = useState("");

  const send = (query) => {
    setInput("");
    var text = query;
    $.ajax({
      type: "POST",
      url: baseUrl + "query?v=2180101",
      contentType: "application/json; charset=utf-8",
      dataType: "json",
      headers: {
        "Authorization": "Bearer " + accessToken
      },
      data: JSON.stringify({ query: text, lang: "en", sessionId: "sam" }),
      success: function(data) {
        var timeout = 0;
        if($.inArray(text, history)){
            timeout = 1000 * ((Math.random() * 2) + 0.5);
        }
        updateHistory(oldArray => [...oldArray, text]);
        setTimeout(setResponse, timeout, data.result.fulfillment.speech);
      }
    });
  }

  const setResponse = (val) => {
    var botString = "Sam-Bot: ";
    var newString = botString + val;
    newString = newString.replace(/\s+/g,' ').trim();
    updateHistory(oldArray => [...oldArray, newString]);

    // $(".chatBox").append("\n" + newString + "\n");
  }


  const classes = useStyles();

  return(


    <Container maxWidth="lg" style={{marginTop: 125, marginBottom: 400}}>


    <Helmet>
      <body style="
      background-image: url('../pbackground.png');
      background-repeat: no-repeat;
      background-size: cover;"
      />
    </Helmet>

    <div style={{textAlign: 'center'}}>

        <Typography variant="h2" style={{fontWeight: 500}}>
          Sam Bot
        </Typography>



        <ChatBody>
          {history.map(function(item){
            return(
              <div>
                {item}
              </div>
            )
          })}
        </ChatBody>

    </div>

      <Input
      onKeyDown={event=>{
        if(event.key === 'Enter'){
          send(input);
        }
      }}
      onChange={event=>{
        setInput(event.target.value);
      }}
      value={input}
      >

      </Input>

      <Button>
        Send
      </Button>

    </Container>

  )
}

export default Home;
