import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import FadeTypography from './FadeTypography';
import { Link } from 'react-router-dom';
import {useSpring, config, animated} from 'react-spring';



const useStyles = makeStyles((theme) => ({
  button: {
    '&:hover': {
        backgroundColor: theme.palette.primary.main,
        fontWeight: 'bold',
    },
    height: 40,
    width: 100,
    right: 30,
    backgroundColor: 'white',
    textTransform: 'none',
  },
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  menuItem: {
    float: 'right',
    display: 'block',
  },
}));




export default function ButtonAppBar() {

    const [showA, setShowA] = React.useState(false);


    const fadeStyles = useSpring({
        config: { ...config.stiff },
        from: { opacity: 0 },
        to: {
          opacity: showA ? 1 : 0
        }
      });

      //////

    const [toggle, setToggle] = React.useState(true);

    function updateBar() {
        setToggle(!toggle);
    }

    const classes = useStyles();

    return (
    <div className={classes.root}>
    <AppBar position="static" elevation={1}>
        <Toolbar>
            <Typography variant="h5" className={classes.title}>
                <Link to="/" style={{color: 'black', textDecoration: 'none'}}>
                    Sam Kirby
                </Link>
            </Typography>

            <animated.span style={fadeStyles}>
                <Button className={classes.button}>
                    <FadeTypography toggled={toggle} className={classes.menuItem}>
                        <Link to="/experience" style={{color: 'black', textDecoration: 'none'}}>
                            Experience
                        </Link>
                    </FadeTypography>
                </Button>
            </animated.span>

            <animated.span style={fadeStyles}>
                <Button className={classes.button}>
                    <FadeTypography toggled={toggle} className={classes.menuItem}>
                        <Link to="/blog" style={{color: 'black', textDecoration: 'none'}}>
                            Blog
                        </Link>
                    </FadeTypography>
                </Button>
            </animated.span>

            <animated.span style={fadeStyles}>
                <Button className={classes.button}>
                    <FadeTypography toggled={toggle} className={classes.menuItem}>
                        <Link to="/contact" style={{color: 'black', textDecoration: 'none'}}>
                            Contact
                        </Link>
                    </FadeTypography>
                </Button>
            </animated.span>



        <IconButton onClick={() => setShowA(val => !val)} edge="start" color="inherit" aria-label="menu">
            <MenuIcon />
        </IconButton>
        </Toolbar>
    </AppBar>
    </div>
  );
}
