import React from 'react';
import { Card, makeStyles } from '@material-ui/core';
import { useSpring } from 'react-spring';


var cardWidth = 250;
var cardHeight = 250;


const useStyles = makeStyles((theme) => ({
  card: {
    backgroundColor: '#EEE4BF',
    boxShadow: '0px 2px 6px #533E2D',
    padding: 10,
    width: cardWidth,
    height: cardHeight,
    marginRight: 50,
    marginLeft: 50,
    marginTop: 50,
    borderRadius: 25,
  },
  icon: {
    marginTop: 15,
    height: 200,
    width: 200,
  },
  title: {
    display: 'inline-block',
    width: 250,
    fontSize: 24,
    textAlign: 'center',
    marginTop: 5,
    fontFamily: 'Montserrat',
    fontWeight: 600
  }
}));
const sideMargins = 20;


function SocialSquare(props){

  var initialColor = '#EEE4BF';
  var hoverColor = '#F0E8CC';


  const [isHovered, setHover] = React.useState(false);

  let onHover = () => {
    setHover(!isHovered)
    console.log(isHovered);
  }

  const classes = useStyles();

  const expand = useSpring({
    config: { friction: 15 },
    // height: isHovered ? `${contentHeight}px` : defaultHeight
  });

  return(
      <div style={{marginLeft: sideMargins, marginRight: sideMargins, display: 'inline-block'}}>
        <Card className={classes.card} onMouseOver={ onHover } onMouseOut={ onHover }
        style={{backgroundColor: isHovered ? hoverColor : initialColor }}>
          <div style={{textAlign: 'center', }}>
            {props.children}
          </div>
        </Card>
        <span className={classes.title}>
          {props.title}
        </span>
      </div>
  )
}

export default SocialSquare;
