import React, { useState, useEffect } from 'react';
import { Card, Typography, Button, IconButton } from '@material-ui/core';
import { useMeasure } from 'react-use';
import { useSpring, animated } from 'react-spring';
import DoubleChevronIcon from '../icons/DoubleChevrons.svg';



const CollapseCard = (props) => {

  const defaultHeight = '650';

  const [open, toggle] = useState(false);

  const [contentHeight, setContentHeight] = useState(defaultHeight);

  const [ref, { height }] = useMeasure();

  useEffect(() => {
    //Sets initial height
    setContentHeight(height);

    //Adds resize event listener
    window.addEventListener("resize", setContentHeight(height));

    // Clean-up
    return window.removeEventListener("resize", setContentHeight(height));
  }, [height]);


  // Animations
  const expand = useSpring({
    config: { friction: 10 },
    height: open ? `${contentHeight}px` : defaultHeight
  });
  const spin = useSpring({
    config: { friction: 10 },
    width: '100%',
    transform: open ? "rotate(180deg)" : "rotate(0deg)"
  });

  return(
    <animated.div ref={ref} style={{margin: 'auto', width: '80%', paddingTop: 30}}>
      <Card style={{width: 650, height: 205}} flexDirection="row">
        <Typography variant="h5" style={{fontWeight: 'bold'}}>
          {props.title}
        </Typography>
        <Typography variant="h6">
          {props.setting}
        </Typography>
        <Typography variant="h6">
          {props.date}
        </Typography>

        <Typography variant="body1" style={{overflow: 'hidden'}}>
          {props.description}
        </Typography>

        <div style={{width: '100%'}}>
          <Button style={{width: '100%'}} disableRipple>
            <animated.div
            onClick={() => toggle(!open)}
            style={spin}>
              <img src={DoubleChevronIcon}/>
            </animated.div>
          </Button>
        </div>

      </Card>
    </animated.div>
  )

}

export default CollapseCard
